var startTime = flow.get("startTime") || "today";
if (startTime === "today" || startTime === "yesterday") {
    if (startTime === "today") {
        var d = new Date();
        d.setHours(0, 0, 0, 0);
        startTime = d.getTime();
    }
    if (startTime === "yesterday") {
        var d = new Date();
        d.setDate(d.getDate() - 1);
        d.setHours(0, 0, 0, 0);
        startTime = d.getTime();
    }
} else {
    startTime = new Date().getTime() - (startTime * 3600 * 1000);
}

var duration = flow.get("duration") || 0;

if (duration > 0) {
    duration = startTime + (duration * 24 * 3600 * 1000);
}


msg.payload = {
    device: flow.get("device"),
    timestamp: {
        $gte: startTime
    }
}

if (duration > 0)
    msg.payload.timestam.$lte = duration

var type = flow.get("class") || "All";
if( type !== "All")
    msg.payload.class = type;

return msg;

msg.topic = "aoi";
msg.payload = flow.get("death") || {
    id: "death",
    x1: 400,
    y1: 400,
