var list = flow.get("list");

var search = flow.get("search");
if( search && search.length ) {
    var searchList = [];
    search = search.toLowerCase();
    list.forEach(function(path){
        var match = false;
        if(path.class.toLowerCase().search(search) >= 0)
            match = true;
        if (path.localTime.search(search) >= 0)
            match = true;
        if( match )
            searchList.push(path);
    });
    list = searchList;
}

var matchBirth = [];
var birth = flow.get("birth");
if( birth ){
    list.forEach(function(path){
        var firstDetection = path.path[0];
        if (firstDetection.x > birth.x1 && firstDetection.x < birth.x2 && firstDetection.y > birth.y1 && firstDetection.y < birth.y2 )
            matchBirth.push(path);
    });
    list = matchBirth;
}


var death = flow.get("death");
var matchDeath = [];
if (death) {
    list.forEach(function (path) {
        var lastDetection = path.path[path.path.length-1];
        if (lastDetection.x > death.x1 && lastDetection.x < death.x2 && lastDetection.y > death.y1 && lastDetection.y < death.y2 )
            matchDeath.push(path);
    });
    msg.payload = matchDeath;
} else {
    msg.payload = list;
}

return msg;

