var device = global.get("device");
if (!device || !device.length)
    return;

var filters = global.get("filters") || {};
global.set("filters", filters);

if (!filters.hasOwnProperty(device))
    return;

var filter = {
    id: flow.get("id") || new Date().getTime(),
    name: flow.get("name"),
    type: flow.get("type"),
    compare: flow.get("compare"),
    value: flow.get("value"),
    active: flow.get("active")
}
node.warn(filter);

var match = null;

filters[device].forEach(function(item) {
    if( item.id === filter.id )
        match=item;
});
if( match ) {
    match.name = filter.name;
    match.type = filter.type;
    match.compare = filter.compare;
    match.value = filter.value;
    match.active = filter.active;
} else 
    filters[device].push(filter);

