var device = global.get("device");
if(!device)
    return;
var filters = global.get("filters");
if( !filters )
    return;
if(!filters.hasOwnProperty(device))
    return;
filters = filters[device];
msg.payload = filters;
if( filters.length === 0)
    return;

var query = {
    device: device
}

var list = [];

filters.forEach(function(item){
    if( item.active !== true ) 
        return;
    var filter = {};
    filter[item.type] = {};
    switch(item.type) {
        case "dx":
        case "dy":
            if (item.compare === "lte") {
                filter[item.type]['$lte'] = item.value;
                filter[item.type]['$gte'] = -item.value;
            }
            if (item.compare === "gte") {
                filter[item.type]['$gte'] = item.value;
                filter[item.type]['$lte'] = -item.value;
            }
            list.push(filter);
            break;
        case "age":
            if (item.compare === "lte")
                filter[item.type] = {
                    '$lte': item.value
                }
            if (item.compare === "gte")
                filter[item.type] = {
                    '$gte': item.value
                }
            list.push(filter);
            break;
    }
})
query['$or'] = list;
msg.payload = query;

return msg;

